			if (p == 'X')S.x[i][j] = 1;
			if (p == '.')S.x[i][j] = 0;
		}
	}
	int R = solve(S); if (R >= 100000)R = -1;
	cout << R << endl;
	return 0;
}

#include <stdio.h>
