typedef long long int ll;
#define BIG_NUM 2000000000
#define MOD 1000000007
#define EPS 0.000001
using namespace std;

int H=19,W=15;

enum DIR{
	Left_Up,
	Up,
	Right_Up,
	Left,
	Right,
	Left_Down,
	Down,
	Right_Down,
};

DIR dir[8] = {Left_Up,Up,Right_Up,Left,Right,Left_Down,Down,Right_Down};

int diff_row[8] = {-1,-1,-1,0,0,1,1,1},diff_col[8] = {-1,0,1,-1,1,-1,0,1};

struct Info{
	char map[19][15];
	int row,col,count;
};

bool rangeCheck(int row,int col){
	if(row <= 17){
		if(col < 0 || col > 14){
			return false;
		}else{
			return true;
		}
	}else{ //row >= 18
		if(row == 18 && (col < 0 || col > 14)){
			return false;
		}else{
			return true;
		}
	}
}


Info move(Info info,DIR dir){
	Info ret;

	for(int i = 0; i < H; i++){
		for(int k = 0; k < W; k++){
			ret.map[i][k] = info.map[i][k];
		}
	}
	ret.count = info.count+1;

	int next_row,next_col,index;

	switch(dir){
	case Left_Up:
		index = 0;
		break;
	case Up:
		index = 1;
		break;
	case Right_Up:
		index = 2;
		break;
	case Left:
		index = 3;
		break;
	case Right:
		index = 4;
		break;
	case Left_Down:
		index = 5;
		break;
	case Down:
		index = 6;
		break;
	case Right_Down:
		index = 7;
		break;
	}

	next_row = info.row+diff_row[index];
	next_col = info.col+diff_col[index];

	while(rangeCheck(next_row,next_col) && info.map[next_row][next_col] == 'X'){
		next_row += diff_row[index];
		next_col += diff_col[index];
	}


	if(next_row == info.row+diff_row[index] && next_col == info.col+diff_col[index]){
		ret.row = 1;
		ret.col = -1;
	}else{
		int tmp_row,tmp_col;

		tmp_row = info.row+diff_row[index];
		tmp_col = info.col+diff_col[index];

		while(tmp_row != next_row || tmp_col != next_col){
			ret.map[tmp_row][tmp_col] = '.';
			tmp_row += diff_row[index];
			tmp_col += diff_col[index];
		}

		ret.row = next_row;
		ret.col = next_col;
	}

	return ret;
}

