#include<bits/stdc++.h>
using namespace std;

const int h=19;
const int w=15;
const int INF=30;

int dh[]={0,-1,-1,-1, 0, 1,1,1};
int dw[]={1, 1, 0,-1,-1,-1,0,1};
bool isRange(int i,int j){
    return 0<=i && i<h && 0<=j && j<w;
}
int dfs(int nh,int nw,vector<string> &f){
    if(nh>=h || isRange(nh,nw) && nh==h-1) return 0;
    if(!isRange(nh,nw)) return INF;
    int res=INF;
    for(int i=0;i<8;i++){
        int toh=nh+dh[i];
        int tow=nw+dw[i];
        if(isRange(toh,tow) && f[toh][tow]=='X'){
            stack<pair<int,int>> st;
            while(isRange(toh,tow) && f[toh][tow]=='X'){
                f[toh][tow]='.';
                st.push({toh,tow});
                toh+=dh[i];
                tow+=dw[i];
            }
            res=min(res,dfs(toh,tow,f)+1);
            while(!st.empty()){
                auto ret=st.top(); st.pop();
                f[ret.first][ret.second]='X';
            }
        }
    }
    return res;
}
int main(){
    vector<string> f(h);
    for(int i=0;i<h;i++) cin>>f[i];
    int inih=-1,iniw=-1;
    for(int i=0;i<h;i++){
        for(int j=0;j<w;j++){
            if(f[i][j]=='O'){
                inih=i,iniw=j;
                f[i][j]='.';
            }
        }
    }

    int res=dfs(inih,iniw,f);
    cout<<(res>=INF ? -1 : res)<<endl;
    return 0;
}

