using namespace std;

const char h = 20, w = 17;
vector<vector<bool>> c;
typedef tuple<pair<char, char>, vector<vector<bool>>, char> T;
queue<T> q;
map<vector<vector<bool>>, vector<vector<char>>> memo;

const int dy[16] = { 0,-1, 0, 1, 1,-1, 1,-1, 0,-2, 0, 2};
const int dx[16] = { 1, 0,-1, 0, 1, 1,-1,-1, 2, 0,-2, 0};

bool impossible(int y, vector<vector<bool>>& c){
	vector<bool> no(h,0);
	range(i,y + 1,18){
		bool f = false;
		rep(j,w){
			if(c[i][j]) f = true;
		}
		if(not f) no[i] = true;
	}
	rep(i,h - 1){
		if(no[i] and no[i + 1]) return false;
	}
	return false;
}

int main(){
	c = vector<vector<bool>>(h, vector<bool>(w,0));

	pair<char, char> s;
	rep(i,h - 1){
		rep(j,w - 2){
			char a;
			cin >> a;
			if(a == 'X') c[i][j + 1] = 1;
			else c[i][j + 1] = 0;
			if(a == 'O'){
				s = make_pair(i,j + 1);
			}
		}
	}

	q.push(T(s,c,0));

	char ans = 127;
	while(not q.empty()){
		char cost;
		tie(s,c,cost) = q.front(); q.pop();

		char y, x;
		tie(y,x) = s;

		//rep(i,h){ rep(j,w){ if(i == y and j == x) cout << 'O'; else cout << c[i][j]; } cout << endl; } cout << endl;

		if(y == 18 or y == 19){
			ans = min(ans, cost);
			continue;
		}

		if(impossible(y, c)) continue;

		if(cost >= 20) continue;

		rep(i,8){
			char ny = y + dy[i];
			char nx = x + dx[i];
			if(ny < 0 || ny >= h || nx < 0 || nx >= w) continue;
			if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)) continue;
			vector<vector<bool>> pc = c;
			if(pc[ny][nx] == 1){
				while(true){
					if(ny < 0 || ny >= h || nx < 0 || nx >= w) {
						ny = -1;
						break;
					}
					if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)){
						ny = -1;
						break;
					}
					if(pc[ny][nx] == 0) break;
					pc[ny][nx] = 0;
					ny = ny + dy[i];
					nx = nx + dx[i];
				}
				if(ny == -1) continue;

				if(memo.count(pc)){
					auto& it = memo[pc];
					if(it[ny][nx] > cost + 1){
						it[ny][nx] = cost + 1;
						q.push(T(make_pair(ny,nx), pc, cost + 1));
					}
				}else{
					vector<vector<char>> dis(h, vector<char>(w,127));
					dis[ny][nx] = cost + 1;
					memo[pc] = dis;
					q.push(T(make_pair(ny,nx), pc, cost + 1));
				}
			}
		}
	}
	cout << (ans == 127 ? -1 : ans) << endl;
}

#include <bits/stdc++.h>

using namespace std;

#define H 19
#define W 15

struct State{
    int x,y,S;
    State(int x,int y,int S) : x(x),y(y),S(S) {}

    bool operator < (const State &s)const{
	if(x != s.x){
	    return x < s.x;
	}else if(y != s.y){
	    return y < s.y;
	}else{
	    return S < s.S;
	}
    }
};

int num[H][W];
char field[H][W];
map<State,int> step;
const int dx[8] = {-1,-1,-1,0,0,1,1,1};
const int dy[8] = {-1,0,1,-1,1,-1,0,1};

inline bool inH(int y){
    return (0 <= y && y < H);
}

inline bool inW(int x){
    return (0 <= x && x < W);
}

inline bool inField(int x,int y){
    return (inH(y) && inW(x));
}

int bfs(int sx,int sy){
    queue<State> Q;
    Q.push(State(sx,sy,0));
    step[State(sx,sy,0)] = 0;

    while(!Q.empty()){
	State s = Q.front(); Q.pop();
	int x = s.x,y = s.y,S = s.S;
	if(y >= H){
	    return step[s];
	}
	for(int i = 0 ; i < 8 ; i++){
	    int nx = x+dx[i], ny = y+dy[i], nS = S;
	    if(!inField(nx,ny)) continue;
	    if(field[ny][nx] != 'X') continue;
	    if(num[ny][nx] >= 0 && !(S >> num[ny][nx] & 1)){
		bool can = true;
		nS |= (1<<num[ny][nx]);
		while(true){
		    nx += dx[i]; ny += dy[i];
		    if(ny >= H || (ny == H-1 && inW(nx))){
			return step[s]+1;
		    }
		    if(!inW(nx) || ny < 0){ can = false; break; }
		    if(field[ny][nx] != 'X') break; 
		    if(nS >> num[ny][nx] & 1) break; 
		    nS |= (1<<num[ny][nx]);
		}
		if(can && step.find(State(nx,ny,nS)) == step.end()){
		    step[State(nx,ny,nS)] = step[s]+1;
		    Q.push(State(nx,ny,nS));
		}
	    }
	}
    }
    return -1;
}

int main(){
    int x,y,n = 0;
    memset(num,-1,sizeof(num));
    for(int i = 0 ; i < H ; i++){
	for(int j = 0 ; j < W ; j++){
	    cin >> field[i][j];
	    if(field[i][j] == 'O'){
		x = j; y = i;
	    }else if(field[i][j] == 'X'){
		num[i][j] = n++;
	    }
	}
    }
    cout << bfs(x,y) << endl;
    return 0;
}

#include <iostream>
