using namespace std;
const int inf = 1e9;
int dx[8] = {1, 1, 1, 0, 0,-1,-1,-1};
int dy[8] = {1, 0,-1, 1,-1, 1, 0,-1};
vector<vector<char> > field(21, vector<char>(17, '.'));
int solve(int sy, int sx){
    if(sy >= 19){
        return 0;
    }
    if(sy <= 0 || 20 <= sy || sx <= 0 || 16 <= sx){
        return inf;
    }
    int ret = inf;
    for(int i=0; i<8; i++){
        int ny = sy +dy[i];
        int nx = sx +dx[i];
        if(field[ny][nx] != 'X') continue;
        while(field[ny][nx] == 'X'){
            field[ny][nx] = '.';
            ny += dy[i];
            nx += dx[i];
        }
        field[sy][sx] = '.';
        field[ny][nx] = 'O';
        ret = min(ret, solve(ny, nx));      
        while(ny != sy && nx != sx){
            field[ny][nx] = 'X';
            ny -= dy[i];
            nx -= dx[i];
        }
        field[ny][nx] = '.';
        field[sy][sx] = 'O';
    }
    return ret+1;
}

int main(){
    int sy, sx;
    for(int i=1; i<=19; i++){
        for(int j=1; j<=15; j++){
            cin >> field[i][j];
            if(field[i][j] == 'O'){
                sy = i;
                sx = j;
            }
        }
    }
    int ans = solve(sy, sx);
    if(ans >= inf){
        cout << -1 << endl;
    }else{
        cout << ans << endl;
    }
    return 0;
}

