#include<bits/stdc++.h>
using namespace std;

struct state{
  int bi,a,b;
  bool operator<(const state& s) const {
    if( bi == s.bi ){
      if( a == s.a ) return b < s.b;
      return a < s.a;
    }
    return bi < s.bi;
  }
};

int dx[]={0,1,0,-1,1,1,-1,-1};
int dy[]={1,0,-1,0,1,-1,1,-1};

map<state,int> dp;
char cie[55][55];
int fie[55][55];
int N,W,H;
bool check( int bi,int x,int y,int d,int &nst,int &nx,int &ny ){
  bool f = false;
  nst = bi;
  nx = x + dx[d];
  ny = y + dy[d];  
  if( nx < 0 || nx >= W || ny < 0 || ny >= H ) return false;
  if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
    f = true;      
    nst |= (1<<fie[nx][ny]);
  }

  while( f ){
    nx = nx + dx[d];
    ny = ny + dy[d];  
    if( ny >= H-1 ) return true;
    if( nx < 0 || nx >= W || ny < 0 ) return false;
    if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
      nst |= (1<<fie[nx][ny]);
    } else
      break;
  }
  return f;
}

int solve(int bi,int x,int y){
  if( dp.count((state){bi,x,y}) != 0 ) return dp[(state){bi,x,y}];
  // cout << bitset<20>(bi) << " "<< x << " "<< y << endl;
  if( y >= H-1 ) return dp[(state){bi,x,y}] = 0;
  int ret = (1<<29);
  for(int i=0;i<8;i++){
    int nx,ny,nst;
    if( check(bi,x,y,i,nst,nx,ny) ){
      ret = min( ret, solve(nst,nx,ny)+1);
    }
  }
  return dp[(state){bi,x,y}] = ret;
}

int main(){
  N = 0;
  W=15;H=19;
  int x,y;
  for(int i=0;i<19;i++){
    for(int j=0;j<15;j++){
      cin >> cie[j][i];
      if( cie[j][i] == 'X')
	fie[j][i] = N++;
      if( cie[j][i] == 'O' ){
	x = j; y = i;
      }	
    }
  }
  int res = solve(0,x,y);
  if( res == (1<<29) )
    cout << -1 << endl;
  else 
    cout << res << endl;
}

#include<bits/stdc++.h>
#define INF (1e9)
#define N 19
#define M 15
using namespace std;
typedef pair<int,int> P;
typedef pair<int,P> P1;
int sy,sx,cnt;
map<P1,int> memo;
map<P,int> num;
string s[N];
int dy[8]={-1,-1,-1,0,0,1,1,1};
int dx[8]={-1,0,1,-1,1,-1,0,1};

int dfs(int A,int y,int x){
  if(memo.count(P1(A,P(y,x))))return memo[P1(A,P(y,x))];
  if(N-1<=y)return 1;
  if(y<0||x<0||N<=y||M<=x)return INF;
  int res=INF;
  memo[P1(A,P(y,x))]=INF;
  for(int i=0;i<8;i++){
    int ny=y+dy[i],nx=x+dx[i],nA=A;
    if(s[ny][nx]!='X')continue;
    while(0<=ny&&ny<N&&0<=nx&&nx<M&&s[ny][nx]=='X'){
      nA|=(1<<num[P(ny,nx)]);
      s[ny][nx]='.';
      ny+=dy[i]; nx+=dx[i];
    }
    res=min(res,dfs(nA,ny,nx));
    while(ny!=y||nx!=x){
      ny-=dy[i]; nx-=dx[i];
      s[ny][nx]='X';
    }
  }
  return memo[P1(A,P(y,x))]=res+1;
}

int main(){
  for(int i=0;i<N;i++){
    cin>>s[i];
    for(int j=0;j<M;j++){
      if(s[i][j]=='O'){
	sy=i,sx=j;
	s[i][j]='.';
      }
      if(s[i][j]=='X')
	num[P(i,j)]=cnt++;
    }
  }
  int ans=dfs(0,sy,sx);
  cout<<(ans==INF+1?-1:ans-1)<<endl;
  return 0;
}

#include<bits/stdc++.h>

#define REP(i,s,n) for(int i=s;i<n;i++)
#define rep(i,n) REP(i,0,n)

using namespace std;

const int IINF = INT_MAX;
const int h = 19;
const int w = 15;

struct Data { 
  int x,y,bitmask,cost;
  bool operator < ( const Data& data ) const { 
    if( cost != data.cost ) return cost < data.cost; 
    if( x != data.x ) return x < data.x;
    if( y != data.y ) return y < data.y;
    if( bitmask != data.bitmask ) return bitmask < data.bitmask;
  }
};

char a[20][20];
//int mindist[1<<20][9];
set<Data> mindist;
int Index[20][20];

bool isValid(int x,int y) { return 0 <= x && x < w && 0 <= y && y < h; }

void compute(){
  int black = 0;
  int sx=-1,sy=-1;
  rep(i,h) rep(j,w) {
    if( a[i][j] == 'X' ) Index[i][j] = black++;
    if( a[i][j] == 'O' ) sx = j, sy = i;
  }
  //rep(i,(1<<black)) rep(j,9) mindist[i][j] = IINF;
  mindist.clear();
  //mindist[(1<<black)-1][4] = 0;
  mindist.insert((Data){sx,sy,(1<<black)-1,0});
  deque<Data> deq;
  deq.push_back((Data){sx,sy,(1<<black)-1,0});
  while( !deq.empty() ){
    Data data = deq.front(); deq.pop_front();
    REP(dx,-1,2){
      REP(dy,-1,2){
        if( dx == 0 && dy == 0 ) continue;
        int nx = data.x + dx, ny = data.y + dy, nbitmask = data.bitmask;
        bool valid = false;
        while( isValid(nx,ny) && ( a[ny][nx] == 'X' && ( data.bitmask & (1<<Index[ny][nx]) ) ) ){
          nbitmask &= ~(1<<Index[ny][nx]);
          nx += dx, ny += dy;
          valid = true;
        }
        if( !isValid(nx,ny) ) {
          if( valid && ( 0 <= nx && nx < w ) && ny >= h ) {
            //cout << mindist[data.bitmask]+1 << endl;
            cout << data.cost+1 << endl;
            return;
          }
          continue;
        }
        if( !valid ) continue;
        //if( mindist[nbitmask] > mindist[data.bitmask] + 1 ) {
        if( !mindist.count((Data){nx,ny,nbitmask,data.cost+1}) ) {
          //mindist[nbitmask] = mindist[data.bitmask] + 1;
          mindist.insert((Data){nx,ny,nbitmask,data.cost+1});
          deq.push_back((Data){nx,ny,nbitmask,data.cost+1});
        }
      }
    }
  }
  puts("-1");
}

int main(){
  rep(i,h) rep(j,w) scanf(" %c",&a[i][j]);
  compute();
  return 0;
}

#include<cstdio>
#include<algorithm>
#include<vector>
#include<queue>

using namespace std;

typedef pair<int, int> P;
typedef pair<P, int> Q;

const int INF = 100000000;

char d[19][16];

int dx[8] = {0, 1, 1, 1, 0, -1, -1, -1};
int dy[8] = {-1, -1, 0, 1, 1, 1, 0, -1};

bool out(int x, int y) {
  return x < 0 || y < 0 || x >= 15 || y >= 19;
}

bool goal(int x, int y) {
  if (y == 18 && !out(x, y)) return true;
  return y >= 19;
}

bool black(int x, int y) {
  return d[y][x] == 'X';
}

void ret(int x, int y, int X, int Y, int j) {
  x += dx[j], y += dy[j];
  while (!(x == X && y == Y)) {
    d[y][x] = 'X';
    x += dx[j], y += dy[j];
  }
}

int solve(int X, int Y, int counter) {
  counter++;
  int ans = INF;
  for (int i = 0; i < 8; i++) {
    int x = X + dx[i], y = Y + dy[i];
    if (out(x, y)) continue;
    if (!black(x, y)) continue;
    while (!out(x, y) && black(x, y)) {
      d[y][x] = '.';
      x += dx[i], y += dy[i];
    }
    if (goal(x, y)) {
      ret(x, y, X, Y, (i + 4) % 8);
      return counter;
    }
    if (out(x, y)) {
      ret(x, y, X, Y, (i + 4) % 8);
      continue;
    }
    ans = min(ans, solve(x, y, counter));
    ret(x, y, X, Y, (i + 4) % 8);
  }
  return ans;
}

