bool is_in(int x,int y){
	return 0<=x&&x<h&&0<=y&&y<w;
}

int solve(){
	queue<pair<pair<int,int>,pair<int,int>>> q;
	vector<bool> used(1<<n,false);
	for(int i=0;i<n;i++){
		for(int j=0;j<8;j++){
			auto now=v[i];
			now.first+=dx[j];
			now.second+=dy[j];
			int nx=now.first,ny=now.second;
			if(is_in(nx,ny)&&fi[nx][ny]=='O'&&!used[(1<<n)-1]){
				q.push(make_pair(make_pair((1<<n)-1,0),make_pair(i,j)));
				used[(1<<n)-1]=1;
			}
		}
	}
	while(q.size()){
		auto p=q.front(); q.pop();
		for(int i=0;i<8;i++){
			bool jump=0;
			auto now=p;
			now.first.second++;
			int nx=v[now.second.first].first+dx[now.second.second];
			int ny=v[now.second.first].second+dy[now.second.second];
			now.second.second=i;
			while(is_in(nx,ny)){
				nx+=dx[i],ny+=dy[i];
				if(!is_in(nx,ny)||mp.find(make_pair(nx,ny))==mp.end()||!(now.first.first>>mp[make_pair(nx,ny)]&1)){
					break;
				}
				jump=true;
				now.second.first=mp[make_pair(nx,ny)];
				now.first.first-=(1<<now.second.first);
			}
			if(!jump)continue;
			if(nx==19||(nx==18&&is_in(nx,ny))){
				return now.first.second;
			}
			if(!is_in(nx,ny))continue;
			if(!used[now.first.first]){
				q.push(now);
				used[now.first.first]=1;
			}
		}
	}
	return -1;
}

