int solve(State p) {
	int cx = 0, cy = 0;
	for (int i = 0; i < 285; i++) {
		if (p.x[i / 15][i % 15] == 2) { cx = i / 15; cy = i % 15; }
	}
	int minx = 999999;
	for (int i = 0; i < 8; i++) {
		State T = p;
		int ex = cx, ey = cy;
		while (true) {
			if (ex < 0 || ey < 0 || ey >= 15)continue;
			ex += dx[i]; ey += dy[i];
			if (ex == 19 || T.x[ex][ey] == 0)break;
			T.x[ex][ey] = 0;
		}
		if (abs(ex - cx) <= 1 && abs(ey - cy) <= 1)continue;
		if (ex == 18 || ex == 19)minx = min(minx, 1);
		else {
			T.x[cx][cy] = 0; T.x[ex][ey] = 2;
			minx = min(minx, solve(T) + 1);
		}
	}
	return minx;
}
