#include <bits/stdc++.h>

using namespace std;

namespace {

    const int INF = 1<<28;

    vector<string> F;
    void input() {
        F.resize(19);
        for (int i = 0; i < 19; i++) cin >> F[i];
    }

    int sy, sx;
    int c;
    vector<vector<int>> N;
    map<int,pair<int,int>> M;

    void init() {
        c = 0;
        sy = sx = -1;
        N.clear(); N.resize(19, vector<int>(15, -1));
        M.clear();
        for (int i = 0; i < 19; i++) {
            for (int j = 0; j < 15; j++) {
                if (F[i][j] == 'O') {
                    sy = i;
                    sx = j;
                }
                if (F[i][j] == 'X') {
                    N[i][j] = c;
                    M[c] = make_pair(i, j);
                    c++;
                }
            }
        }
    }

    struct Pos {
        int y, x;
        int used;
        Pos(int y, int x, int used) : y(y), x(x), used(used) {}
        Pos() {}
    };
    bool operator<(const Pos& a, const Pos& b) {
        if (a.y == b.y) {
            if (a.x == b.x) return a.used < b.used;
            else return a.x < b.x;
        } else {
            return a.y < b.y;
        }
    }
    map<Pos,int> memo;

    Pos findNextPos(int y, int x, int dy, int dx, int used) {
        int nused = used;
        while (true) {
            int ny = y + dy;
            int nx = x + dx;
            if (ny < 0 || ny >= 19 || nx < 0 || nx >= 15) return Pos(ny, nx, nused);
            if (F[ny][nx] == 'X') {
                int id = N[ny][nx];
                if (nused & (1 << id)) {
                    return Pos(ny, nx, nused);
                } else {
                    nused |= (1 << id);
                }
            } else {
                return Pos(ny, nx, nused);
            }
            y = ny;
            x = nx;
        }
    }

    bool movable(int y, int x, int dy, int dx, int used) {
        int ny = y + dy;
        int nx = x + dx;
        if (ny < 0 || ny >= 19 || nx < 0 || nx >= 15) return false;
        if (F[ny][nx] != 'X') return false;
        int id = N[ny][nx];
        if (used & (1 << id)) return false;
        return true;
    }

    int dfs(int y, int x, int used) {
        //cout << "dfs: " << y << " " << x << " " << used << endl;
        if (y >= 18) return 0;
        if (y < 0 || x < 0 || x >= 15) return INF;
        Pos key(y, x, used);
        if (memo.count(key)) return memo[key];
        int ans = INF;
        for (int dy = -1; dy <= 1; dy++) {
            for (int dx = -1; dx <= 1; dx++) {
                if (dy == 0 && dx == 0) continue;
                if (not movable(y, x, dy, dx, used)) continue;
                Pos next = findNextPos(y, x, dy, dx, used);
                ans = min(ans, dfs(next.y, next.x, next.used) + 1);
            }
        }
        return memo[key] = ans;
    }

    void solve() {
        init();
        int ans = dfs(sy, sx, 0);
        if (ans >= INF) ans = -1;
        cout << ans << endl;
    }
}

int main() {
    input();
    solve();
}

#include <bits/stdc++.h>
#define r(i,n) for(int i=0;i<n;i++)

using namespace std;

typedef pair<int,int>P;
typedef pair<P,int>P2;

string s[21];
int h=21,w=17,sx,sy;
int dx[]={0,1,1,1,0,-1,-1,-1};
int dy[]={-1,-1,0,1,1,1,0,-1};
bool d[21][19][1<<20];

map<pair<int,int>,int>M1;
map<int,pair<int,int> >M2;
int cnt;

int main(){
  s[0]=s[20]=".................";
  r(i,h-2){
    cin>>s[i+1];
    s[i+1]="."+s[i+1]+".";
  }
  r(i,h)r(j,w)if(s[i][j]=='O'){
    sx=j;
    sy=i;
    s[i][j]='.';
  }else if(s[i][j]=='X'){
    M2[cnt]=P(i,j);
    M1[P(i,j)]=cnt++;
  }
  queue<P2>q;
  d[sy][sx][0]=1;
  q.push(P2(P(sy*1000+sx,0),0));
  while(!q.empty()){
    P2 p=q.front();q.pop();
    int y=p.first.first/1000;
    int x=p.first.first%1000;
    int cost=p.first.second;
    int bit=p.second;
