      int ny=y+dy[i];
      int nx=x+dx[i];
      int b=bit;
      if(ny<=0||nx<=0||ny>=h||nx>=w-1)continue;
      if(s[ny][nx]=='.')continue;
      if((1<<M1[P(ny,nx)])&b)continue;
      while(s[ny][nx]=='X'){
	int c=M1[P(ny,nx)];
	if((1<<c)&b)break;
	b+=(1<<c);
	ny+=dy[i];
	nx+=dx[i];
      }
      if(ny<20&&(nx==0||nx==w-1))continue;
      if(d[ny][nx][b]==1)continue;
      d[ny][nx][b]=1;
      q.push(P2(P(ny*1000+nx,cost+1),b));
      if(ny>=19){
	cout<<cost+1<<endl;
	return 0;
      }
    }
  }
  cout<<-1<<endl;
}

