#define max(x,y) (((x)>(y))?(x):(y))
#define min(x,y) (((x)<(y))?(x):(y))
#define W 15
#define H 19

char ban[H+1][W+1];
char line[W+1];
int dir[3]={-1,0,1};
int dirx[8]={1,1,0,-1,-1,-1, 0, 1};
int diry[8]={0,1,1, 1, 0,-1,-1,-1};

void white_stone(char ban[][W+1],int *yp,int *xp)
{
  int i,j;
  for(i=0;i<H;i++)
    for(j=0;j<W;j++)
      if(ban[i][j]=='O')
	{ *yp=i,*xp=j;
	  return;
	}
}

int direct_goal(char ban[][W+1],int y,int x)
{
  int i,k,l,ret;

  if(y==H-2)
    if((x>0 && ban[y+1][x-1]=='X')||ban[y+1][x]=='X'||
       (x<W-1 && ban[y+1][x+1]=='X'))
      return(1);
    else
      return(0);

  for(i=0;i<3;i++)
    {
    for(k=y+1,l=x+dir[i];;k++,l+=dir[i])
      {
	if(k >= H-1)
	  return(1);
	if((k==H-2 && (l<=0 || l>=W-1)) || ban[k][l] != 'X')
	  goto NEXT;
      }
    NEXT: ;
    }
  return(0);
}

int can_jump(char ban[][W+1],int d,int y,int x)
{
  int k,l,ren;


  ren=0;
  for(k=y+diry[d],l=x+dirx[d];;k+=diry[d],l+=dirx[d])
    {

      if(l<0 || l>W-1 || k<0 || k> H-1)
	return(0);
      else if(ban[k][l]!='X')
	break;
      else
	ren++;
    }
  if(ren)
    return(1);

  return(0);
}

void jump(char ban[][W+1],int d,int *yp,int *xp)
{
  int k,l,y,x;

  y=*yp,x=*xp;
  ban[y][x]='.';
  for(k=y+diry[d],l=x+dirx[d];;k+=diry[d],l+=dirx[d])
    {
      if(ban[k][l]=='X')
	ban[k][l]='.';
      else
	{
	  ban[k][l]='O';
	  break;
	}
    }
  *yp=k,*xp=l;
}
void print_ban(char ban[][W+1])
{
  int i;
  printf("\n");
  for(i=0;i<H;i++)
    printf("%s\n",&ban[i][0]);
  printf("\n");
}

int solve(char ban[][W+1],int y,int x)
{
  int i,min_,d,y0,x0;
  char my_ban[H+1][W+1];

  if(direct_goal(ban,y,x))
    return(1);

  min_=1000;
  for(d=0;d<8;d++)
    {

      if(can_jump(ban,d,y,x))
      {
	//printf("dir=%d OK|",d);
	memcpy(my_ban,ban,sizeof(my_ban));
	y0=y,x0=x;
	jump(my_ban,d,&y0,&x0);
	//print_ban(my_ban);
	min_=min(min_,1+solve(my_ban,y0,x0));
      }
    }
  return(min_);
}


