          break;
        }
        if (field[ny][nx] != 'X' || ((bits >> index[ny][nx]) & 1) == 0) {
          break;
        }
        flag |= true;
        nbits &= ~(1 << index[ny][nx]);
      }
      if (flag) {
        min = Math.min(min, dfs(nx, ny, nbits) + 1);
      }
    }
//    done[y][x][bits] = true;
//    return memo[y][x][bits] = min;
    return min;
  }

