  int dfs(int x, int y, int bits) {
//    debug(x, y, Integer.toBinaryString(bits));
    if (y >= 19) {
      return 0;
    }
    if (x < 0 || 15 <= x || y < 0) {
      return INF;
    }
//    if (done[y][x][bits]) {
//      return memo[y][x][bits];
//    }
    int min = INF;
    for (int[] d : ofs) {
      int nx = x;
      int ny = y;
      int nbits = bits;
      boolean flag = false;
      for (; ; ) {
        nx += d[0];
        ny += d[1];
        if (nx < 0 || 15 <= nx || ny < 0 || 19 <= ny) {
          break;
        }
        if (field[ny][nx] != 'X' || ((bits >> index[ny][nx]) & 1) == 0) {
          break;
        }
        flag |= true;
        nbits &= ~(1 << index[ny][nx]);
      }
      if (flag) {
        min = Math.min(min, dfs(nx, ny, nbits) + 1);
      }
    }
//    done[y][x][bits] = true;
//    return memo[y][x][bits] = min;
    return min;
  }

