  int dfs(int x, int y, int bits) {
//    debug(x, y, Integer.toBinaryString(bits));
    if (y >= 18) {
      return 0;
    }
    if (x < 0 || 15 <= x || y < 0) {
      return INF;
    }
//    if (done[y][x][bits]) {
//      return memo[y][x][bits];
//    }
    int min = INF;
    for (int[] d : ofs) {
      int nx = x;
      int ny = y;
      int nbits = bits;
      boolean flag = false;
      for (; ; ) {
        nx += d[0];
        ny += d[1];
        if (nx < 0 || 15 <= nx || ny < 0 || 19 <= ny) {
          break;
        }
        if (field[ny][nx] != 'X' || ((bits >> index[ny][nx]) & 1) == 0) {
          break;
        }
        flag |= true;
        nbits &= ~(1 << index[ny][nx]);
      }
      if (flag) {
        min = Math.min(min, dfs(nx, ny, nbits) + 1);
      }
    }
//    done[y][x][bits] = true;
//    return memo[y][x][bits] = min;
    return min;
  }

  void run() {
    for (int[] a : index) Arrays.fill(a, -1);
    int count = 0;
    int sx = -1, sy = -1;
    for (int i = 0; i < 19; ++i) {
      String str = sc.next();
      for (int j = 0; j < 15; ++j) {
        field[i][j] = str.charAt(j);
        if (field[i][j] == 'X') {
          index[i][j] = count++;
        }
        if (field[i][j] == 'O') {
          sx = j;
          sy = i;
        }
      }
    }
    int v = dfs(sx, sy, (1 << count) - 1);
    System.out.println(v == INF ? -1 : v);
  }

