      ans = score + 1
      return
    if mp[ny + dy][nx + dx] == "#":
      continue
    if mp[ny + dy][nx + dx] == "." and use:
      for ux, uy in use:
        mp[uy][ux] = "."
      search(nx + dx, ny + dy, score + 1)
      for ux, uy in use:
        mp[uy][ux] = "X"

