llint memo[55][55][25][30];

llint calc(int i, int j, int k, int l)
{
	//cout << i << j << k << l << endl;

	if(memo[i][j][k][l] != -1) return memo[i][j][k][l];
	if(i > j) return 1;
	if(l > 26) return 0;
	if(k > 21) return 0;

	if(l == 0){
		if(c[i][k] == 0){
			if(i+1 == j && c[i+1][k] == 0) return 0;
			else return memo[i][j][k][l] = calc(i+1, j, k, 0);
		}
		else return memo[i][j][k][l] = calc(i, j, k, 1);
	}

	llint ret = 0;
	ret += calc(i, j, k, l+1);
	for(int x = i; x <= j; x++){
		if(c[x][k] != -1){
			if(c[x][k] < l) return memo[i][j][k][l] = 0;
			if(c[x][k] > l) break;
		}
		if(x < j) ret += calc(i, x, k+1, 0) * calc(x+1, j, k, l+1) % mod;
		else ret += calc(i, j, k+1, 0) % mod;
		ret %= mod;
	}
	//cout << i << j << k << l << " " << ret << endl;
	return memo[i][j][k][l] = ret;
}

