				long long Q = ((dp[i][j] * P) % mod)*solve(pos + 1, j + 1, k);
				dp[i + 1][k] += Q; dp[i + 1][k] %= mod;
			}
		}
	}
	res[pos][l][r] = dp[27][r] + 1;
	return res[pos][l][r] - 1;
}

