inline int solve(int l, int r, int k) {
	if(r - l == 1) {
		int ret = 1;
		for(int i = k; i < s[l].size(); i++) {
			if(s[l][i] == '?') ret = 1LL * ret * 26 % mod;
		}
		return ret;
	}
	if(dp[l][r][k]) return dp[l][r][k] - 1;
	int dp2[51][27] = {0}; dp2[l][0] = 1;
	for(int a = l + 1; a <= r; a++) {
		for(int b = 1; b <= 26; b++) {
			for(int c = a - 1; c >= l; c--) {
				bool flag1 = true, flag2 = true;
				for(int d = c; d < a; d++) {
					if((s[d][k] != '?' && s[d][k] != b + 96) || (d != c && s[d].size() == k + 1)) {
						flag1 = false; break;
					}
				}
				if(!flag1) continue;
				int res = flag2 ? solve(c, a, k + 1) : 1, r = 0;
				for(int d = b - 1; d >= 0; d--) {
					dp2[a][b] = (dp2[a][b] + 1LL * dp2[c][d] * res) % mod;
					r = (r + 1LL * dp2[c][d] * res) % mod;
				}
			}
		}
	}
	int ret = 0;
	for(int i = 1; i <= 26; i++) ret += dp2[r][i];
	dp[l][r][k] = ret + 1;
	return ret;
}
