const int mod = 1000000007;
struct Mod {
public:
	int num;
	Mod() : num(0) {}
	Mod(long long int n) : num((n % mod + mod) % mod) { ; }
	operator int() { return num; }

private:

};

Mod operator+(const Mod a, const Mod b) { return Mod((a.num + b.num) % mod); }
Mod operator+(const long long int a, const Mod b) { return Mod(a) + b; }
Mod operator++(Mod &a) { return a + Mod(1); }
Mod operator-(const Mod a, const Mod b) { return Mod((mod + a.num - b.num) % mod); }
Mod operator-(const long long int a, const Mod b) { return Mod(a) - b; }
Mod operator--(Mod &a) { return a - Mod(1); }
Mod operator*(const Mod a, const Mod b) { return Mod(((long long)a.num * b.num) % mod); }
Mod operator*(const long long int a, const Mod b) { return Mod(a)*b; }
Mod operator+=(Mod &a, const Mod b) { return a = a + b; }
Mod operator+=(long long int &a, const Mod b) { return a = a + b; }
Mod operator-=(Mod &a, const Mod b) { return a = a - b; }
Mod operator-=(long long int &a, const Mod b) { return a = a - b; }
Mod operator*=(Mod &a, const Mod b) { return a = a * b; }
Mod operator*=(long long int &a, const Mod b) { return a = a * b; }
Mod operator^(const Mod a, const int n) {
	if (n == 0) return Mod(1);
	Mod res = (a * a) ^ (n / 2);
	if (n % 2) res = res * a;
	return res;
}
Mod inv(const Mod a) { return a ^ (mod - 2); }
Mod operator/(const Mod a, const Mod b) {
	assert(b.num != 0);
	return a * inv(b);
}
Mod operator/(const long long int a, const Mod b) {
	assert(b.num != 0);
	return Mod(a) * inv(b);
}
Mod operator/=(Mod &a, const Mod b) {
	assert(b.num != 0);
	return a = a * inv(b);
}

#define MAX_MOD_N 1024000

Mod fact[MAX_MOD_N], factinv[MAX_MOD_N];
void init() {
	fact[0] = Mod(1); factinv[0] = 1;
	for (int i = 0; i < MAX_MOD_N - 1; ++i) {
		fact[i + 1] = fact[i] * Mod(i + 1);
		factinv[i + 1] = factinv[i] / Mod(i + 1);
	}
}
Mod comb(const int a, const int b) {
	return fact[a] * factinv[b] * factinv[a - b];
}


Mod memo[51][51][51][51];

bool decide[51][51][51][51];

vector<string>sts;
Mod getans(const int l, const int r,const int place, const int num) {
	if (decide[l][r][place][num]) {
		return memo[l][r][place][num];
	}
	else {
		decide[l][r][place][num] = true;

		if (l == r) {
			return memo[l][r][place][num]=Mod(1);
		}

		else if (l + 1 == r) {
			Mod ans(1);
			if (sts[l][place] == '?') {
				ans *= min(26,26 - num+1);
			}
			else if (sts[l][place] < 'a' + num-1) {
				return memo[l][r][place][num] = 0;
			}
			for (int n = place+1; n < sts[0].size(); ++n) {
				if (sts[l][n] == '?') {
					ans *= 26;
				}
			}
			return memo[l][r][place][num] = ans;
		}
