LL solve(int b, int e, int p, int lb, int ub){
  int& ret = dp[b][e][p][lb][ub];
  if(ret >= 0) return ret;
  if(b == e) return ret = 1;
  if(lb >= ub) return ret = 0;
  if(b+1 == e){
	if(SZ(vs[b]) <= p) return ret = 1;
	LL res = 0;
	if(vs[b][p] == '?'){
	  res = ub - lb;
	}
	else{
	  int c = vs[b][p] - 'a';
	  if(!(lb <= c && c < ub))
		return ret = 0;
	  res = 1;
	}
	for(int i=p+1;i<SZ(vs[b]);++i)
	  if(vs[b][i] == '?')
		(res *= 26) %= MOD;
	return ret = res;
  }

  ret = 0;
  LL res = 0;
  int c = -1;
  for(int i=b;i<e;++i){
	if(SZ(vs[i]) <= p){
	  break;
	}
	else if(vs[i][p] != '?'){
	  int nc = vs[i][p] - 'a';
	  if(c < 0) c = nc;
	  else if(c != nc)
		c = 27;
	}
	if(c == 27)
	  break;
	if(c != -1){
	  if(lb <= c && c < ub)
		(res += solve(b, i+1, p+1, 0, 26) * solve(i+1, e, p, c+1, ub) % MOD) %= MOD;
	}
	else{
	  for(int c=lb;c<ub;++c){
		(res += solve(b, i+1, p+1, 0, 26) * solve(i+1, e, p, c+1, ub) % MOD) %= MOD;
	  }
	}
  }
  return ret = res;
}

