Int dfs(Int u,Int d,Int k,Int a){
  Int &res=dp[u][d][k][a];
  if(~res) return res;
  res=0;

  if(u+1==d&&k==(Int)s[u].size())
    return res=1;

  if(k==(Int)s[u].size()&&k<(Int)s[u+1].size())
    return res=dfs(u+1,d,k,0);

  for(Int i=u;i<d;i++)
    if(k>=(Int)s[i].size())
      return res=0;

  Int q=check(u,d,k,'?'-'a');

  for(Int c=a;c<26;c++)
    if(q||check(u,d,k,c)){
      (res+=dfs(u,d,k+1,0))%=MOD;
      if(!q) break;
    }

  q=s[u][k]=='?';
  for(Int i=u+1;i<d;i++){
    for(Int c=a;c<26;c++){
      if(q||check(u,i,k,c)){
	(res+=dfs(u,i,k+1,0)*dfs(i,d,k,c+1)%MOD)%=MOD;
	if(!q) break;
      }  
    }
    q&=s[i][k]=='?';
  }

  //if(res) cout<<u<<" "<<d<<" "<<k<<" "<<a<<":"<<res<<endl;
  return res;
}

