LL solve(int b, int e, int p, int lb, int ub){
  LL& res = dp[b][e][p][lb][ub];
  if(res >= 0) return res;
  if(b == e) return res = 1;
  if(lb >= ub) return res = 0;
  if(b+1 == e){
	if(vs[b][p] == '?'){
	  res = ub - lb;
	}
	else{
	  int c = vs[b][p] - 'a';
