	if(c != -1){
	  if(lb <= c && c < ub)
		(res += solve(b, i, p+1, 0, 26) * solve(i, e, p, c+1, ub) % MOD) %= MOD;
	}
	else{
	  for(int c=lb;c<ub;++c){
		(res += solve(b, i, p+1, 0, 26) * solve(i, e, p, c+1, ub) % MOD) %= MOD;
	  }
	}
  }
