//s[l]~s[r-1]の位置posの値が全てval以上である
lli solve(int l, int r, int pos, int val){
	lli &ret = dp[l][r][pos][val];
	if(used[l][r][pos][val]) return ret;
	used[l][r][pos][val] = true;
	if(l == r || (r-l==1 && pos >= 20)){
		ret = 1;
		return ret;
	}
	if(val == 27 || pos >= 20){
		ret = 0;
		return ret;
	}

	ret = 0;
	//val と val以上 に分割
	for(int i=l; i<=r; i++){
		if(i != l){
			//前半はちょうどvalのみ
			if(!(s[i-1][pos]==val || s[i-1][pos]=='?')) break;
			//?は空白にはなれない
			if(val==0 && s[i-1][pos]=='?') break; 
		}
		ret += solve(l, i, pos+1, 0) *solve(i, r, pos, val+1) %mod;
	}
	ret %= mod;
	return ret;
}

