		else if (l + 1 == r) {
			Mod ans(1);
			if (sts[l][place] == '?') {
				ans *= min(26,26 - num+1);
			}
			else if (sts[l][place] < 'a' + num-1) {
				return memo[l][r][place][num] = 0;
			}
			for (int n = place+1; n < sts[0].size(); ++n) {
				if (sts[l][n] == '?') {
					ans *= 26;
				}
			}
			return memo[l][r][place][num] = ans;
		}
		else {
			for (int n = l; n < r; ++n) {
				if (sts[n][place] >= 'a' + num - 1 || sts[n][place] == '?') {

				}
				else {
					return memo[l][r][place][num] = Mod(0);
				}
			}
			Mod ans(0);
			if (place == sts[0].size() - 1 && num == 26) {
				return memo[l][r][place][num] = Mod(0);
			}else if (place == sts[0].size() - 1) {
				if (sts[l][place] == 'a' + num - 1 || sts[l][place] == '?') {
					ans += getans(l + 1, r, place, num + 1);
				}
				ans += getans(l, r, place, num + 1);
