ll saiki(int a,int b,int c,int d){
	if(d>=27||c>=21)return 0;
	if(dp[a][b][c][d]!=0)return dp[a][b][c][d];
	if(a==b&&c==20)return 1;
	ll ans = saiki(a,b,c,d+1);
	for(int i=a;i<=b;i++){
		bool flag = 1;
		if(d==0&&v[i][c]==0){
			if(i==b){
				ans = (ans + saiki(a,i,c+1,0))%mod;
			}else{
				ans = (ans + saiki(a,i,c+1,0)*saiki(i+1,b,c,d+1))%mod;
			}
			flag = 0;
		}
		if(d!=0&&v[i][c]=='?'){
			if(i==b){
				ans = (ans + saiki(a,i,c+1,0))%mod;
			}else{
				ans = (ans + saiki(a,i,c+1,0)*saiki(i+1,b,c,d+1))%mod;
			}
			flag = 0;
		}
		if(d!=0&&v[i][c]==d-1+'a'){
			if(i==b){
				ans = (ans + saiki(a,i,c+1,0))%mod;
			}else{
				ans = (ans + saiki(a,i,c+1,0)*saiki(i+1,b,c,d+1))%mod;
			}
			flag = 0;
		}
		if(flag)break;
	}
	return dp[a][b][c][d] = ans;
}

