int calc(int y,int x){
  if(y < 0) return 1;
  int res = 1;
  for(;x<(int)str[y].size();x++) if(str[y][x]=='?') res=(res*26)%mod; 
  return res;
}

int dfs(int y,int x,int u){
  if(y == n) return calc(y-1,x+1);
  if(x >= (int)str[y].size()) return 0;
  if(used[y][x][u]++) return mem[y][x][u];

  int p = str[y][x];
  int res = 0;
  if(isalpha(p)){
    p-=s;
    if(p > u)  res = (res+calc(y-1,x+1)*dfs(y+1,x,p))%mod;
    if(p == u) res = (res+dfs(y-1,x+1,0))%mod;
  }
  else if(p == '?'){
    for(p=1;p<=26;p++){
      if(p > u)  res = (res+calc(y-1,x+1)*dfs(y+1,x,p))%mod;
      if(p == u) res = (res+dfs(y-1,x+1,0))%mod;
    }
  }
  return mem[y][x][u] = res;
}


