#define mod 1000000007
using namespace std;
int n, p[21], c[50][21], dp[50][51][21]; string s[50];
int solve(int l, int r, int k) {
	if(r - l == 1) return p[c[l][k]];
	if(dp[l][r][k]) return dp[l][r][k] - 1;
	int dp2[51][27] = {0}; dp2[l][0] = 1;
	for(int a = l + 1; a <= r; a++) {
		for(int b = 1; b <= 26; b++) {
			for(int c = a - 1; c >= l; c--) {
				if((s[c][k] != '?' && s[c][k] != b + 96) || (c + 1 != a && s[c].size() == k + 1)) break;
				int res = solve(c, a, k + 1), r = 0;
				for(int d = b - 1; d >= 0; d--) {
					dp2[a][b] = (dp2[a][b] + 1LL * dp2[c][d] * res) % mod;
					r = (r + 1LL * dp2[c][d] * res) % mod;
				}
			}
		}
	}
	int ret = 0;
	for(int i = 1; i <= 26; i++) ret += dp2[r][i];
	dp[l][r][k] = ret + 1;
	return ret;
}
