ll dp[52][52][22][30];

ll solve(int up,int down,int id,int c){

  ll &ret = dp[up][down][id][c];
  if( ret != -1 ) return ret;
  ret = 0;

  if( up > down ) return ret = 1LL;
  if( c >= 27 ) return ret = 0LL;
  if( up == down && id == S[up].size() ) return ret = 1LL;

  char ch = c + 'a' - 1;
  if( S[up][id] != '?' ) {
    if( S[up][id] < ch ) return ret = 0;
    ch = S[up][id]; 
  } else {
    ch = max( 'a', ch );
    ret = solve(up,down,id,max(2,c+1));
  }
  /*
  if( up == down ){
    return ret = (solve(up,down,id+1,0) * (S[up][id] == '?'?26LL:1LL)) % MOD;
  }
  */
  int ci = ch - 'a' + 1;

  int cnt = up;
  for(int i=up;i<=down;i++){
    if( S[i][id] == ch ) cnt = i;
  }
  if( (int)S[up].size() == id+1 ){
    if( cnt != up ) return ret = 0LL;
    ret += ( solve( up + 1, down, id, ci + 1 ) * solve(up,up,id+1,0) ) % MOD;
    ret %= MOD;
  } else {
    for(int i=cnt;i<=down;i++){
      if( S[i][id] == '?' || S[i][id] == ch ){
        ret += ( solve(i+1,down,id,ci + 1) * solve(up,i,id+1,0) ) % MOD;
        ret %= MOD;
      } else 
        break;
    }
  }

  //   cout << up << " " << down << " " << id << " "<< c << " ( " << ch << " ) "  << S[up][id] << endl;  
  //  cout << ret << endl;
  return ret;
}

