  if(str[y][x] == '?') k = 27 - ch;
  if(k == 0) return 0;
  static int mem[N][22]={},used[N][22]={};
  if(used[y][x]) return (k*mem[y][x])%mod;
  used[y][x] = 1;
  int &res = mem[y][x] = 1;
  for(++x;x<(int)str[y].size();x++) if(str[y][x]=='?') res = (res*26)%mod;
  return (res*k)%mod;
}


