  usedc[u][d][x][ch-s] = 1;
  for(int y=u;y<=d;y++) if(str[y][x] != '?' && str[y][x] != ch) return memc[u][d][x][ch-s]=0;
  return  memc[u][d][x][ch-s]=1;
}

