  if(used[u][d][x][c]++) return mem[u][d][x][c];

  vector<int> mnc(N,30);
  for(int y=d;y>=u;y--)mnc[y] = min(mnc[y+1],str[y][x]=='?'? 30:str[y][x] - s);  

  int res = dfs(u,d,x,c+1); 
