  for(int y=u;y<=d;y++){
    if(mnc[y+1] <= c || !check(u,y,x,c+s)) continue;
    res = (res+(dfs(u,y,x+1,1)*dfs(y+1,d,x,c+1))%mod)%mod;
  }
    return mem[u][d][x][c] = res;
}


