#define S 21
#define L 28
#define N 51
#define M 1000000007

char s[N][S];
int f[N][N][S][L], n;

int solve(int l, int r, int p, int c)
{
    if (~f[l][r][p][c])
        return f[l][r][p][c];
    if (l >= r)
        return 1;
    if (c == L - 1)
        return 0;
    if (p == S - 1)
        return f[l][r][p][c] = l + 1 == r;

    int a = 0;

    for (int m = l; m <= r; m++)
    {
        a = (a + 1LL * solve(l, m, p + 1, 0) * solve(m, r, p, c + 1) % M) % M;
        if (m == r || (s[m][p] < L - 1 && s[m][p] != c) || (s[m][p] == L - 1 && !c))
            break;
    }

    return f[l][r][p][c] = a;
}

