		}
	}

	for(int i = 1; i <= 51; i++){
		for(int j = 1; j <= 51; j++){
			for(int k = 0; k < 25; k++){
				for(int l = 0; l < 30; l++){
					memo[i][j][k][l] = -1;
				}
			}
		}
	}

	cout << calc(1, n, 0, 0) << endl;
	return 0;
}

