int dp[50][51][25][128];

int rec(int top, int bottom, int left, int x) {
  if(top >= bottom || x > 'z') return 0;
  if(left == 20) return top + 1 == bottom;
  if(~dp[top][bottom][left][x]) return dp[top][bottom][left][x];
  int &ret = dp[top][bottom][left][x];
  ret = rec(top, bottom, left, x + 1);
  for(int i = top; i < bottom; i++) {
    if((x < 'a' || S[i][left] != '?') && S[i][left] != x) return ret;
    ret += 1LL * rec(top, i + 1, left + 1, 'a' - 2) * rec(i + 1, bottom, left, x + 1) % mod;
    ret %= mod;
  }
  ret += rec(top, bottom, left + 1, 'a' - 2);
  ret %= mod;
  return ret;
}

