int N;
ll dp[51][51][21][27];
char table[50][21];
char base = 'a'-1;

ll recursive(int left,int right,int loc,int ch){

	if(dp[left][right][loc][ch] != -1)return dp[left][right][loc][ch];

	if(left >= right)return 1;

	if(ch == 27){

		return 0;

	}else if(loc == 20){

		if(left+1 == right){
			return dp[left][right][loc][ch] = 1;
		}else{
			return dp[left][right][loc][ch] = 0;
		}
	}

	ll ret = 0;
	ll tmp;

	for(int mid = left; mid <= right; mid++){

		if((mid != left) && ((table[mid-1][loc] != base+ch && table[mid-1][loc] != '?') ||
				(table[mid-1][loc] == '?' && ch == 0)))break;

		tmp = recursive(left,mid,loc+1,0)*recursive(mid,right,loc,ch+1);

		tmp %= MOD;
		ret += tmp;
		ret %= MOD;
	}

	return dp[left][right][loc][ch] = ret;
}


