	      dp[L][R][num] = 1;
	      for(int j=num-1;j<s[L].size();j++) if(s[L][j]=='?') dp[L][R][num] = (dp[L][R][num]*26LL)%mod;
	      return dp[L][R][num];
	}
	int cnt = 0;
