		if(s[i].size() < num){
			if(i != L || cnt) return dp[L][R][num] = 0;
			cnt++;
		}
	}
	if(cnt){
		if(cnt == R-L+1){
			return dp[L][R][num] = 1;
		}
		else return dp[L][R][num] = rec(L+cnt,R,num);
