					}
				}
			}
			else{
				ll R = rec(j+1,i,num+1);
				ll T = 0;
				for(int k=1;k<=26;k++){
					T = (T+t[j][k-1])%mod;
					t[i][k] = (t[i][k]+T*R%mod)%mod;
				}
			}
		}
	}
	for(int i=1;i<=26;i++) dp[L][R][num] = (dp[L][R][num]+t[R][i])%mod;
