  int c = -1;
  for(int i=b;i<e;++i){
	if(SZ(vs[i]) <= p){
	  c = 27;
	  break;
	}
	else if(vs[i][p] != '?'){
	  int nc = vs[i][p] - 'a';
	  if(c < 0) c = nc;
	  else if(c != nc)
		c = 27;
	}
	if(c == 27)
	  break;
	if(c != -1){
	  if(lb <= c && c < ub)
		(res += solve(b, i+1, p+1, 0, 26) * solve(i+1, e, p, c+1, ub) % MOD) %= MOD;
	}
	else{
	  for(int c=lb;c<ub;++c){
		(res += solve(b, i+1, p+1, 0, 26) * solve(i+1, e, p, c+1, ub) % MOD) %= MOD;
	  }
	}
  }
  return ret = res;
}

