ModInt<> solve(int l, int r, int idx, int chr) {
  if (r - l <= 0) return r == l;
  if (chr == 26) return 0;
  if (reached[l][r][idx][chr]) return dp[l][r][idx][chr];
  reached[l][r][idx][chr] = 1;
  ModInt<> res = 0;
  for (int i = l + 1; i < r; ++i)
    if (s[i].size() <= idx) return dp[l][r][idx][chr] = 0;
  if (s[l].size() <= idx) return dp[l][r][idx][chr] = solve(l + 1, r, idx, chr);
  for (int mid = l; mid <= r; ++mid) {
    res += solve(l, mid, idx + 1, 0) * solve(mid, r, idx, chr + 1);
    if (mid == r) break;
    if (s[mid][idx] != '?' && s[mid][idx] - 'a' != chr) break;
  }
  return dp[l][r][idx][chr] = res;
}

