namespace Program
{
    public class Solver
    {

        public void Solve()
        {
            const int L = 20;
            const char ZERO = (char)('a' - 1);
            var n = sc.Integer();
            var s = Enumerate(n, x => sc.Scan());
            for (int i = 0; i < n; i++)
                while (s[i].Length < L) s[i] += ZERO;
            //(i,j),k,[a,b]
            var dp = new ModInteger[n, n, L, 27];
            for (int i = 0; i < n; i++)
                for (int j = 0; j < n; j++)
                    for (int k = 0; k < L; k++)
                        for (int l = 0; l < 27; l++)
                            dp[i, j, k, l].num = -1;
            Func<int, int, int, int, ModInteger> dfs = null;
            dfs = (l, r, i, a) =>
              {
                  if (l > r) return 1;
                  if (i == L)
                      return r == l ? 1 : 0;
                  if (a >= 27) return 0;
                  if (dp[l, r, i, a].num != -1) return dp[l, r, i, a];
                  //Debug.WriteLine($"[{l} {r}] {i} {(char)(a + 'a')}");
                  ModInteger ret = 0;
                  ret += dfs(l, r, i, a + 1);
                  for (int m = l; m <= r; m++)
                  {
                      if (a == 0 && s[m][i] == '?') break;
                      if (s[m][i] != '?' && s[m][i] != a + ZERO) break;
                      ret += dfs(l, m, i + 1, 0) * dfs(m + 1, r, i, a + 1);
                  }
                  return dp[l, r, i, a] = ret;
              };
            var ans = dfs(0, n - 1, 0, 0);
            IO.Printer.Out.WriteLine(ans);
        }


        public IO.StreamScanner sc = new IO.StreamScanner(Console.OpenStandardInput());
        static T[] Enumerate<T>(int n, Func<int, T> f) { var a = new T[n]; for (int i = 0; i < n; ++i) a[i] = f(i); return a; }
        static public void Swap<T>(ref T a, ref T b) { var tmp = a; a = b; b = tmp; }
    }
