using namespace std;

typedef pair<int, int> P;
typedef pair<P, P> Q;
typedef pair<int, Q> R;

double diff(P a, P b) {
	double x = (double)a.first-(double)b.first;
	double y = (double)a.second-(double)b.second;
	return sqrt(x*x+y*y);
}

double ff(int a) {
	return (double)a/60.0;
}

int main() {
	int n;
	cin >> n;
	vector<R> data(n);
	int f, a, t, x, y;
	for (int i = 0; i < n; i++) {
		cin >> f >> a >> t >> x >> y;
		data[i] = R(f, Q(P(a, t), P(x, y)));
	}
	double amax, at, bmax, bt;
	amax = at = bmax = bt = 0.0;
	for (int i = 0; i < n-1; i++) {
		//cout << 'b' << endl;
		if ((((data[i].second).first).second == ((data[i+1].second).first).second) && (((data[i].second).first).first != ((data[i+1].second).first).first)) {
			//cout << ((data[i].second).first).first << ' ' <<  ((data[i+1].second).first).first << endl;
			if (((data[i].second).first).second == 0) {
				if (diff((data[i].second).second, (data[i+1].second).second) > amax) {
					amax = diff((data[i].second).second, (data[i+1].second).second);
					at = ff(data[i+1].first-data[i].first);
				} else if (diff((data[i].second).second, (data[i+1].second).second) == amax) {
					at = min(at, ff(data[i+1].first-data[i].first));
				}
			} else {
				if (diff((data[i].second).second, (data[i+1].second).second) > bmax) {
					bmax = diff((data[i].second).second, (data[i+1].second).second);
					bt = ff(data[i+1].first-data[i].first);
				} else if (diff((data[i].second).second, (data[i+1].second).second) == bmax) {
					bt = min(bt, ff(data[i+1].first-data[i].first));
				}
			}
		}
	}
	if (amax == 0.0) {
		cout << -1 << ' ' << -1 << endl;
	} else {
		printf("%.10f %.10f\n", amax, at);
	}
	if (bmax == 0.0) {
		cout << -1 << ' ' << -1 << endl;
	} else {
		printf("%.10f %.10f\n", bmax, bt);
	}
	return 0;
}

