void solve(int team) {
    int ans_d = 0, ans_t = 1e8;
    rep(i, N) if (i) {
        if (a[i - 1] == a[i]) continue;
        if (t[i - 1] != t[i]) continue;
        if (t[i - 1] != team) continue;
        int d = pow2(x[i - 1] - x[i]) + pow2(y[i - 1] - y[i]);
        int diff = f[i] - f[i - 1];
        if (ans_d < d) {
            ans_d = d;
            ans_t = diff;
        } else if (ans_d == d && ans_t > diff) {
            ans_d = d;
            ans_t = diff;
        }
    }
    if (ans_t == 1e8) {
        cout << "-1 -1" << endl;
    } else {
        printf("%.20f %.20f\n", sqrt(ans_d), ans_t / 60.0);
    }
}
