bool eq(double a, double b){
  return abs(a-b) < EPS;
}

int main(void){
  int N;
  double dist_A = -1.0, dist_B = -1.0;
  int time_A = MAX_TIME, time_B = MAX_TIME;
  cin >> N;
  int pref = -1, prea = -1, pret = -1;
  double prex = -1.0, prey = -1.0;
  while(N--){
    int f, a, t, x, y;
    cin >> f >> a >> t >> x >> y;
    if(pret == t && prea != a && prex != x && prey != y){
      if(t == 0){
        double dist = sqrt(pow(prex - x, 2) + pow(prey - y, 2));
        if(eq(dist_A, dist)){
          time_A = min(time_A, f - pref);
        }else{
          dist_A = max(dist_A, sqrt(pow(prex - x, 2) + pow(prey - y, 2)));
          time_A = f - pref;
        }
      }else{
        double dist = sqrt(pow(prex - x, 2) + pow(prey - y, 2));
        if(eq(dist_B, dist)){
          time_A = min(time_B, f - pref);
        }else{
          dist_B = max(dist_B, sqrt(pow(prex - x, 2) + pow(prey - y, 2)));
          time_B = f - pref;
        }
      }
    }
    pret = t; prea = a; pref = f; prex = x; prey = y;
  }
  if(eq(dist_A, -1.0)){
    cout << -1 << " ";
  }else{
    printf("%f ", dist_A);
  }
  if(time_A == MAX_TIME){
