  return sqrt(x*x + y*y);
}

double flame(int current, int prev){
  return (double)(current - prev)/60;
}

int main(int argc, char *argv[]){
  int n;
  double T[2] = {INF, INF}, dist[2] = {0};
  cin >> n;
  data d[n];
  for (int i = 0; i < n; i++) {
    cin >> d[i].f >> d[i].a >> d[i].t >> d[i].x >> d[i].y;
  }
  for (int i = 1; i < n; i++) {
    if(d[i - 1].t != d[i].t)continue;
