    if(dist[d[i].t] < Dist(d[i].x - d[i - 1].x, d[i].y - d[i - 1].y)){
      T[d[i].t] = flame(d[i].f, d[i - 1].f);
      dist[d[i].t] = Dist(d[i].x - d[i - 1].x, d[i].y - d[i - 1].y);
    }else if(dist[d[i].t] == Dist(d[i].x - d[i - 1].x, d[i].y - d[i - 1].y)){
      T[d[i].t] = min(flame(d[i].f, d[i - 1].f), T[d[i].t]);
