class Ball {
  public:
    int frame, player, team, y, x;
    Ball(int _frame, int _player, int _team, int _y, int _x) {
      frame = _frame, player = _player, team = _team, y = _y, x = _x;
    }
    Ball() {}
};

double distanceBalls(Ball, Ball);
int frameBalls(Ball, Ball);

int main() {
  int n;
  cin >> n;
  vector<pair<double, int> > moveInAs = {make_pair(-1, -60)};
  vector<pair<double, int> > moveInBs = {make_pair(-1, -60)};
  Ball lball;
  for(int i = 0; i < n; ++i) {
    int frame, player, team, y, x;
    cin >> frame >> player >> team >> x >> y;
    Ball ball(frame, player, team, y, x);
    if(i > 0 && lball.team == ball.team && lball.player != ball.player) {
      if(ball.team == 0) moveInAs.push_back(make_pair(distanceBalls(lball, ball), frameBalls(lball, ball)));
      if(ball.team == 1) moveInBs.push_back(make_pair(distanceBalls(lball, ball), frameBalls(lball, ball)));
    }
    lball = ball;
  }
  auto comPair = [](const pair<double, int> &left, const pair<double, int> &right) {
    return ((left.first == right.first) ? (left.second < right.second) : left.first > right.first);
  };
  sort(begin(moveInAs), end(moveInAs), comPair);
  sort(begin(moveInBs), end(moveInBs), comPair);
  cout << moveInAs[0].first << ' ' << (double)moveInAs[0].second / 60.0 << endl;
