  return 0;
}

double distanceBalls(Ball a, Ball b) {
  return sqrt(pow(a.x - b.x, 2) + pow(a.y - b.y, 2));
}

int frameBalls(Ball a, Ball b) {
  return abs(a.frame - b.frame);
}

